import React from 'react';
import GlobalStyle from 'styles/globalStyles';
import { Sidebar, Header, ScrollToTop, ProgressBar, HelmetElement, useFetch } from 'components';
import { useTranslation } from 'react-i18next';
import { useAppDispatch, useAppSelector } from 'store/hooks';
// import { ErrorsHandler } from 'libs/handlers';
import Pages from 'pages';
import { Endpoints } from 'libs';
import { selectAuthentication } from 'store/selectors';


function App() {
  const dispatch = useAppDispatch();
  const { i18n } = useTranslation();
  const { token } = useAppSelector(selectAuthentication);
  const global = useAppSelector(state => state.global);
  return (
    <>

      <HelmetElement />
      <GlobalStyle $language={i18n.language} $global={global} />
      <ProgressBar />
      <Header is_loading={false} language={i18n.language} />
      <Sidebar />
      <Pages />
      <ScrollToTop />
      {/* <ErrorsHandler /> */}



    </>);
}

export default App;
