import React from "react";
import Link from "next/link";
import { connect } from 'react-redux'
import { withTranslation } from "react-i18next";
import { AppRoutes } from 'libs/routes';
import { setGlobalParams } from "store/slicers/global";
import { AuthenticationTypes } from "libs/types";
import { SVG, Loader, Modal } from "components";
import { AppsList } from 'libs/constants'

type Props = {
    authentication: AuthenticationTypes
    global: {
        header: {
            [key: string]: string
        }
        sidebar: {
            [key: string]: string
        }
    }
    language: string
    t: (text: string) => string;
    hideSidebar: () => void;
    showSidebar: () => void;
}

interface State {
    isOpen: boolean;
    loginModal: boolean;
    logoutModal: boolean;
}
class Header extends React.Component<Props, State> {
    constructor(props: Props) {
        super(props);
        this.state = {
            isOpen: false,
            loginModal: false,
            logoutModal: false
        }
    }

    sidebarToggle = (): void => {
        if (this.props.global.sidebar.isVisible) {
            this.props.hideSidebar();
        } else {
            this.props.showSidebar();
        }
    }

    headerCloser(e: MouseEvent, state: State): void {
        const header = document.getElementById("HeaderHeader00_nav") as HTMLElement;
        const el = e.target as HTMLElement;
        //to close the header when user clicks outside the header and header is opened
        if (state.isOpen && !header.isEqualNode(el) && !header.contains(el)) {
            this.setState({ isOpen: false });
        }
    }
    componentWillUnmount(): void {
        document.removeEventListener("click", (e: MouseEvent) => this.headerCloser(e, this.state))
    }
    componentDidMount(): void {
        document.addEventListener('click', (e: MouseEvent) => this.headerCloser(e, this.state))
    }
    render() {
        const { t } = this.props;
        const { isOpen } = this.state;
        const { header, sidebar } = this.props.global;
        const { profile, isAuthenticated, loading } = this.props.authentication;
        return (
            <>
                <header className={isOpen ? "app_header_isOpen" : "app_header"} id="HeaderHeader00_nav">
                    <div className="left-content">
                        <div className="buttons" onClick={() => { this.setState({ isOpen: false }); this.sidebarToggle(); }} title={t(sidebar.isVisible ? "Hide sidebar" : "Show sidebar")}>
                            <SVG class="sidebar-toggle-svg" name={`caret-${sidebar.isVisible ? 'down' : 'up'}`} color={header.color} />
                        </div>
                        <Link onClick={() => this.setState({ isOpen: false })} className="buttons" href={AppRoutes.home} id="HeaderLogo">
                            <SVG name="global" color={header.color} />
                            <span id="world_of_technology_logo_title">{t("World Of Technology")}</span>
                        </Link>
                        <button className="buttons" type="button" id="btn-menue" onClick={() => this.setState({ isOpen: !isOpen })}>
                            <SVG color={header.color} name={isOpen ? "bars-staggered-solid" : "bars-solid"} />
                        </button>
                    </div>
                    <div className="right-content">
                        {AppsList.filter(item => item.is_public).map((app, index: number) => {
                            return (
                                <Link href={app.path} onClick={() => this.setState({ isOpen: false })} className='buttons' key={index}>
                                    {app.icon ?
                                        <SVG name={app.icon} color={header.color} />
                                        : null}
                                    <span>{t(app.title)}</span>
                                </Link>
                            )
                        })}
                        {isAuthenticated && profile ? <>
                            <div className="buttons">
                                <span className="profile_image">
                                    <img src={profile.image.cdn} alt={""} />
                                </span>
                            </div>
                            <div className={`dropdown ${['ar'].includes(this.props.language) ? 'leftDropdown' : 'rightDropdown'}`}>
                                <Link onClick={() => this.setState({ isOpen: false })} className='dropdown-link' href={AppRoutes.myProfile} title="Profile">
                                    <SVG name="user" color={header.color} />
                                    <span>{t("Profile")}</span>
                                </Link>
                                <div className='dropdown-link' title={t("Log out")} onClick={() => this.setState({ logoutModal: true })}>
                                    <SVG name="right-from-bracket" color={header.color} />
                                    <span>{t("Log out")}</span>
                                </div>
                            </div>
                        </> : <>
                            {loading ?
                                <span className="buttons"> <Loader size={30} borderWidth={3} /> </span> :
                                <div className='buttons' title={t("Log in")} onClick={() => this.setState({ loginModal: true })}>
                                    <SVG name="right-to-bracket" color={header.color} />
                                </div>
                            }
                        </>}
                    </div>
                </header >
                <Modal
                    isVisible={this.state.loginModal}
                    setIsVisible={(value: boolean) => this.setState({ "loginModal": value })}
                    form="login"
                    title="Login"
                    closeButton={true}
                />
                <Modal
                    form="logout"
                    title="logout"
                    isVisible={this.state.logoutModal}
                    setIsVisible={(value: boolean) => { this.setState({ logoutModal: value }) }}
                />
            </>
        )
    }
}

function mapStateToProps(state: any, ownProps: { is_loading: boolean, language: string }) {
    return {
        authentication: state.authentication,
        global: state.global,
        language: ownProps.language
    }
}

const mapDispatchToProps = (dispatch: any) => ({
    setGlobalParams: () => dispatch(setGlobalParams({ param: "", key: "", value: "" })),
    hideSidebar: () => dispatch(setGlobalParams({ param: "sidebar", key: "isVisible", value: false })),
    showSidebar: () => dispatch(setGlobalParams({ param: "sidebar", key: "isVisible", value: true })),
})

export default connect(mapStateToProps, mapDispatchToProps)(withTranslation()(Header));
