import React, { useEffect, useState } from "react";
import { useAppDispatch, useAppSelector } from "store/hooks";
import { SVG, Modal } from "components";
import { useTranslation } from "react-i18next";
import { setGlobalParams } from "store/slicers/global";
import {  selectSidebar } from "store/selectors";


function Sidebar() {
    const [searchModal, setSearchModal] = useState<boolean>(false);
    const [languagesModal, setLanguagesModal] = useState<boolean>(false);
    const [settingsModal, setSettingsModal] = useState<boolean>(false);
    const [notificationModal, setNotificationsModal] = useState<boolean>(false);
    const [messagesModal, setMessagesModal] = useState<boolean>(false);
    const [fullScreen, setFullScreen] = useState<boolean>(typeof document !== 'undefined' ? document.fullscreenEnabled : false);
    const [query, setQuery] = useState<string>("");
    const { t } = useTranslation('translation');
    const sidebar = useAppSelector(selectSidebar);
    const [sidebarStatus, setSidebarStatus] = useState<null | typeof sidebar>(null);
    const dispatch = useAppDispatch();
    const [darkLightMode, setDarkLightMode] = useState<boolean>(sidebar.isVisibleBg);


    const directionToggle = (): void => {
        if (sidebar.direction === 'right') {
            dispatch(setGlobalParams({ param: 'sidebar', key: 'direction', value: 'left' }));
        } else {
            dispatch(setGlobalParams({ param: 'sidebar', key: 'direction', value: 'right' }));
        }
    }
    const fullscreenToggle = (): void => {
        const el = document.documentElement as HTMLElement;
        function fullscreen() {
            if (el.requestFullscreen) {
                el.requestFullscreen();
                setFullScreen(true);
            }
        }
        function existFullscreen() {
            if (document.fullscreenElement) {
                document.exitFullscreen()
                    .catch(er => console.log(er));
                setFullScreen(false);
            }
        }
        if (window.fullScreen && window.screen.height === window.innerHeight) {
            existFullscreen();
        }
        else if (window.screen.height !== window.innerHeight) {
            fullscreen();
        }

    }

    useEffect(() => {
        setSidebarStatus(sidebar)
        window.onkeydown = (event) => {
            if (event.key === 'Escape' || event.key === 'F11') {
                event.preventDefault();
                fullscreenToggle();
            }
        }
    }, [sidebar]);
    if (!sidebarStatus || (sidebarStatus && !sidebarStatus.isVisible)) return null;
    return (
        <>
            <div className={`sidebar ${sidebar.direction}`}>
                <button type="button" className="sidebar-buttons" onClick={() => directionToggle()} title={t("Switch the sidebar direction")}>
                    <SVG name={`arrow-${sidebar.direction === 'right' ? 'left' : 'right'}`} color={sidebar.color} />
                </button>
                <button type="button" className="sidebar-buttons" onClick={() => setSearchModal(!searchModal)} title={t('Search')}>
                    <SVG name="search" color={sidebar.color} />
                </button>
                <button type="button" className='sidebar-buttons' onClick={() => setDarkLightMode(true)} title={t('Dark-light mode')} id="sidebar-darkLightMode-button">
                    <SVG name='circle-half-stroke' color={sidebar.color} />
                </button>
                <button type="button" className="sidebar-buttons" onClick={() => fullscreenToggle()} title={t("Exit fullscreen") || t("Fullscreen")}>
                    <SVG name={fullScreen ? "compress-solid" : "expand-solid"} color={sidebar.color} />
                </button>
                <button type="button" className="sidebar-buttons" onClick={() => setLanguagesModal(!languagesModal)} title={t("Choose your language")}>
                    <SVG name='language' color={sidebar.color} />
                </button>
                <button type="button" className="sidebar-buttons" onClick={() => setSettingsModal(!settingsModal)} title={t("Settings")}>
                    <SVG name='gear-solid' color={sidebar.color} />
                </button>

            </div>
            <Modal isVisible={searchModal}
                title="Search"
                setIsVisible={setSearchModal}
                form="search"
                {...{ query, setQuery, sender: "search" }}
            />
            <Modal isVisible={languagesModal}
                title="Choose your language"
                setIsVisible={setLanguagesModal}
                form="translation"
            />
            <Modal isVisible={settingsModal}
                title="Settings"
                closeButton={true}
                setIsVisible={setSettingsModal}
                form="settings"
                border={"specialColor"}
                // fullScreen={true}
                background={"bgNone"}
            />
            <Modal isVisible={darkLightMode}
                title="Dark light mode"
                closeButton={true}
                setIsVisible={setDarkLightMode}
                form="dark-light-mode"
                // fullScreen={true}
            />


        </>
    )
}

export default Sidebar;
