import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { Visitor } from 'libs/handlers';
import { VisitorTypes } from 'libs/types';

type globalStateTypes = VisitorTypes['global_settings'];

const visitor = new Visitor();
const settings = visitor.getGlobalSettings();

export const initialState: globalStateTypes = {
    scrollToTop: {
        isVisible: settings.scrollToTop.isVisible
    },
    pathBar: {
        isVisible: settings.pathBar.isVisible
    },
    progressBar: {
        isVisible: settings.progressBar.isVisible,
        backgroundColor: settings.progressBar.backgroundColor,
    },
    body: {
        fontFamily: settings.body.fontFamily,
        backgroundColor: settings.body.backgroundColor,
        opacity: settings.body.opacity,
    },
    goals: {
        progressBarVisibility: true,
    },
    header: {
        color: settings.header.color,
        backgroundColor: settings.header.backgroundColor,
        opacity: settings.header.opacity,
        marginsColor: settings.header.marginsColor,
    },
    sidebar: {
        color: settings.sidebar.color,
        backgroundColor: settings.sidebar.backgroundColor,
        opacity: settings.sidebar.opacity,
        direction: settings.sidebar.direction,
        isVisible: settings.sidebar.isVisible,
        isVisibleBg: settings.sidebar.isVisibleBg,
        hoverColor: settings.sidebar.hoverColor
    },
    footer: {
        color: settings.footer.color,
        backgroundColor: settings.footer.backgroundColor,
        opacity: settings.footer.opacity,
        marginsColor: settings.footer.marginsColor,
    },

    controlPanel: {
        isVisible: true
    }
}
const globalSlicer = createSlice({
    name: 'global',
    initialState,
    reducers: {
        setGlobalParams(state, actions: PayloadAction<{ param: any, key: any, value: string | number | boolean | number[] }>) {
            console.log('second: ')
            function setValues<P extends keyof globalStateTypes, K extends keyof globalStateTypes[P], V extends globalStateTypes[P][K]>(param: P, key: K, value: V) {
                state[param][key] = value;
                visitor.setGlobalSettings(state);
            }
            setValues(actions.payload.param, actions.payload.key, actions.payload.value);
        },

    }
})

export const { setGlobalParams } = globalSlicer.actions
export default globalSlicer.reducer;
