import React, { useEffect, useState } from 'react';
import 'styles/common/modal.scss';
import { useTranslation } from 'react-i18next';
import { Forms } from '../forms'
import {  FormsNames, ModalTypes } from 'libs/types';

interface Props {
    isVisible: boolean;
    form: FormsNames 
    setIsVisible: (value: boolean) => void;
    closeButton?: boolean;
    header?: boolean;
    title?: string;
    maxWidth?: ModalTypes['maxWidth'];
    background?: "bgBlur" | "bgDark" | "bgNone" | "bgClosing";
    border?: "borderColor" | "specialColor";
}

export default function Modal(props: Props) {
    const { isVisible, closeButton, header, title, form, setIsVisible, background,   maxWidth, border } = props;
    const [modal, setModal] = useState<ModalTypes>({ form: form, background, header, closeButton, title,  maxWidth, border });
    const { t:t_, i18n } = useTranslation('translation');
    const t = (text:string):string => t_(text)
    const rtl: boolean = ['ar'].includes(i18n.language) ? false : true;
    const hideModal = (element: HTMLDivElement): void => {
        if ((element.hasAttribute('class') && element.className && element.className.toString().includes('modal-container')) ||
            (element.hasAttribute("class") && element.className.toString().includes('close'))) {
            setIsVisible(false);
        }
    }
    useEffect(() => {
        window.onkeydown = (e: KeyboardEvent): void => {
            if (e.key === 'Escape' && isVisible) {
                setIsVisible(false);
            }
        }
    }, [modal, isVisible, setIsVisible]);
    if (!isVisible) return null;
    return (
        <div className={`modal-container ${background ? background : 'bgDark'}`} onClick={(e) => hideModal((e.target as HTMLDivElement))} >
            <div className={`content-container ${modal.maxWidth ? modal.maxWidth : 'maxWidth600'} ${modal.border}`}>
                {modal.header || modal.title || modal.closeButton ?
                    <div className='modal-header-container'>
                        {modal.title ?
                            <div className={`title ${rtl ? "capitalizeFirstLetter" : ""}`}><span>{t(modal.title)}</span></div>
                            : null}
                        {modal.closeButton && modal.closeButton !== undefined ?
                            <div onClick={(e) => hideModal((e.target as HTMLDivElement))}>
                                <span className='close' title={t('Close the window')}>&times;</span>
                            </div>
                            : null}
                    </div>
                    : null}
                <div className='body-container'>
                    <Forms modal={modal} setModal={setModal} props={props} />
                </div>
            </div>
        </div>
    )
}
